$(document).ready((function(){$.ajax({url:"https://randomuser.me/api/?results=10",dataType:"json",success:function(e){for(var a=0;a<e.results.length;a++)$(".accordion").append('<div class="accordion__item traveler"> \n        <a><div class="traveler__image"> \n          <img src="'+e.results[a].picture.medium+'" class="rounded-circle"> \n          </div> \n          <div class="traveler__name-location"> \n            <p class="traveler__name"><span>'+e.results[a].name.first+"</span> <span>"+e.results[a].name.last+'</span></p>\n            <p class="traveler__location"><span>'+e.results[a].location.city+"</span>, <span>"+e.results[a].location.state+'</span></p>\n          </div> \n        </a> \n        <div class="content traveler__contact"> \n            <div class="traveler__phone"> \n                <p class="traveler__phone--label">Phone</p> \n                <p class="traveler__phone--number">'+e.results[a].phone+'</p>\n            </div> \n            <div class="traveler__email"> \n                <p class="traveler__email--label">Email</p> \n                <p class="traveler__email--address">'+e.results[a].email+"</p> \n            </div> \n        </div> \n      </div>");function s(){this.classList.toggle("active"),this.nextElementSibling.classList.toggle("active")}document.querySelectorAll(".accordion__item a").forEach(e=>e.addEventListener("click",s))}})}));